import React from 'react';
import '../Styles/Header.css'
import {useState} from 'react';

const Header = () => {


    const [userName, setUserName] = useState("");
    const [tweet, setTweet] = useState("");
    const [people, setPeople] = useState([]);
    const [showInput, setInput] = useState(false);
  
    const submit = () => {
      setPeople([...people, {userName: userName, tweet: tweet}]);
      setUserName("");
      setTweet("");
      }

    const onClick = () => {setInput(true)}


    const createTweet = () => {(
        <div>
        <input value = {userName} onChange={e => setUserName(e.target.value)} placeHolder="input your name"/>
        <input value = {tweet} onChange={e => setTweet(e.target.value)} placeHolder="Tweet"/>

        {people.map((person, i) =>  (<h3>{person.userName}, {person.tweet}</h3>))}
        </div>
    )}

  
    return (
        <div>
            <div id = "header">
                <h1>Twitter</h1>
                <p>Profile Name</p>
                <p>3/19/2023</p>
                <p>Trending</p>
                <button onClick={onClick}> Enter</button>
                {showInput ? createTweet : null}
            </div>  
        <br></br>  
      </div>
  
    );
}

export default Header;
