import React from 'react';
import {useState} from 'react';
import '../Styles/Tweet.css';
import profile from '../Images/pfp.png';

const Counter = () => {
    const [count, setCount] = useState(0)

    let upCount = ()  => {
        setCount(count+1);
    }
    let downCount = ()  => {
        (count> 0) ? setCount(count-1): setCount(0);
    }
}

const Tweet = (props) => {

    let profileName = "Tanush";

    return(
        <div>
            <div id="container">

            <img id="img" src={profile}></img>

                <b id="profile">{props.profile}</b>
                <b id="text">{props.text}</b>
                <b>@{profileName}</b>
                <div id="extra">
                    <p>my tweet text</p>
                    <p>3/19/2023</p>
                    <p>3 likes, 4 retweets</p>
                </div>

            </div>
        </div>

    );
}

export default Tweet;
